import Button from '../Button';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { Redirect } from 'react-router-dom';
import {useHistory} from "react-router-dom";


var baseURL = 'http://127.0.0.1:5000/token:qwhu67fv56frt5drfx45e/admins/';
export function ShowAdmins({ adminList }) {
  return (
    <div className="">
      <h1 className="pb-2">List Of Admins</h1>
      <table className="table table-striped">
        <tbody className="">
          <tr>
            <th>Id</th>
            <th>Full Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Password</th>
            <th>
              <Link to="new">
                <Button
                  color=""
                  bgColor="var(--primary-color)"
                  name="Add Admin"
                  id="addAdminBtn"
                  className="btn px-4"
                />
              </Link>
            </th>
          </tr>
        </tbody>
        <tbody id="tableDataField">
          {adminList.map((item) => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.fullName}</td>
                <td>{item.phone}</td>
                <td>{item.email}</td>
                <td>{item.password}</td>
                <td>
                  <Link to="edit">
                    <Button
                      color=""
                      bgColor=""
                      name="Edit"
                      id={item.id}
                      className="btn editBtn"
                    />
                  </Link>

                  <Button
                    color=""
                    bgColor=""
                    name="Delete"
                    id={item.id}
                    className="btn deleteButton ms-1"
                    onclick={() => DeleteAdmin(item.id)}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export function NewAdmin() {
  const [id, setId] = useState('123');
  const [fullName, setFullName] = useState('eyob abel');
  const [phone, setPhone] = useState('1445ss1178956');
  const [email, setEmail] = useState('abel@gmail.com');
  const [passwordHash, setPasswordHash] = useState('jaflnsdhe');
  const [defaultAdmin, setDefaultAdmin] = useState(false);

  const history = useHistory();


  let data = {
    id,
    fullName,
    phone,
    email,
    passwordHash,
    defaultAdmin,
  };

  function newAdminAction() {

    let headersList = {
      Accept: '/Application/json',
      'Content-Type': 'application/json',
      // "mode": 'no-cors',
    };

    let bodyContent = JSON.stringify({
      id,
      fullName,
      phone,
      email,
      passwordHash,
      defaultAdmin,
    });

    fetch('http://127.0.0.1:5000/token:qwhu67fv56frt5drfx45e/admins/', {
      method: 'POST',
      body: bodyContent,
      headers: headersList,
    })
      .then(function (response) {
        return response.text();
      })
      .then(function (data) {
        console.log(data);
      });

      // window.location.replace("/")
      history.goBack
  }
  return (
    <form
      className="form"
      id="add-admin-form"
      action=""
      method=""
      onSubmit={(e) => {
        newAdminAction();
        e.preventDefault();
        // console.log(data)
      }}
    >
