import Button from '../Button';
import { Link, useLocation } from 'react-router-dom';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as ReactDOM from 'react-dom/client';


// import Add from "./functions"

import ErrorDisplay from '../ErrorDisplay';
import { CheckApiResponse } from '../functions/CheckApiResponse';
import { Hash } from '../functions/Hash';
import HashPassword from '../functions/HashPassword';

var baseURL = 'http://127.0.0.1:5000/token:qwhu67fv56frt5drfx45e/admins';
export function ShowAdmins({ adminList }) {
  return (
    <div className="">
      <h1 className="pb-2">List Of Admins</h1>
      <table className="table table-striped">
        <tbody className="">
          <tr>
            <th>Id</th>
            <th>Full Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Password</th>
            <th>
              <Link to="new">
                <Button
                  color=""
                  bgColor="var(--primary-color)"
                  name="Add Admin"
                  id="addAdminBtn"
                  className="btn px-4"
                />
              </Link>
            </th>
          </tr>
        </tbody>
        <tbody id="tableDataField">
          {adminList.map((item) => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.fullName}</td>
