import React from "react";
import ToggleButton from "./componentsToggleButton";

class Clock extends React.Component{

    constructor(props){
        super(props);
        this.state={ date: new Date()}

    }

    componentDidMount(){
        this.timerID=setInterval(() => {
            this.setState(
                {date : new Date()}
            )
        }, 1000);
    }

    componentWillUnmount(){
        clearInterval(this.timerID) 
    }



    render(){
        return <div>
        <h1>{this.state.date.toLocaleString()},{this.props.message}</h1>
        <ToggleButtonon/>
        </div>
    }

}

export default Clock;
