import React, { useEffect, useState } from 'react';
import Button from '../Button';
import * as ReactDOM from 'react-dom/client';


import '../../css/login.css';
import '../../css/common.css';
import { useNavigate } from 'react-router-dom';
import ErrorDisplay from '../ErrorDisplay';

export default function LoginPage() {
  const [phoneNo,setPhoneNo]=useState("")
  const [password,setPassword]=useState("")
  const token = "token:qwhu67fv56frt5drfx45e"

  function checkApiResponse(apiResponse) {

    console.log(apiResponse.status)
    const root = ReactDOM.createRoot(document.getElementById('errorDisplay'));

    switch (apiResponse.status) {
      case 200:
        root.render(<ErrorDisplay design="text-success" message="Success" />);

        break;

        case 400:
          root.render(<ErrorDisplay design="text-danger" message="You have incomplete credentials" />);

          break;

        case 404:
          root.render(<ErrorDisplay design="text-danger" message="You have invalid credentials, check user" />);

          break;

      default:
        break;
    }

    if (apiResponse.status < 500 && apiResponse.status >= 400) {
      console.log(apiResponse.status);
      root.render(
        <ErrorDisplay
          design="text-danger"
          message="Your request wasn't accepted try check the fields and make sure the branch wasn't already registered"
        />
      );
    } else if (apiResponse.status < 300 && apiResponse.status >= 200) {

    } else if (apiResponse.status < 600 && apiResponse.status >= 500) {
      root.render(
        <ErrorDisplay
          design="text-danger"
          message="Sorry we're currently experiencing technical difficulties"
        />
      );
    }
  }

  function submitFunc(){
    let headersList = {
      "Accept": "/Application/json",
      "Content-Type": "application/json"
     }

     let bodyContent = JSON.stringify({
         "phone": "abelAbel",
         "passwordHash" : "abel"
     });

     fetch("http://127.0.0.1:5000/token:qwhu67fv56frt5drfx45e/admins/login?phone=abelAbel&passwordHash=abel", { 
       method: "POST",
       body: bodyContent,
       headers: headersList
     }).then(function(data) {
      //  console.log(data);
       checkApiResponse(data)
     })
    //------------

    //  
    // 
    // write
    // 
    // 
    // localStorage.setItem('loggedIn',true)
    //  window.location.reload()
    //  navigate("/")
  }

  const navigate = useNavigate();
  return (
    <div className="container h-100">
      <div className="row h-100 justify-content-center align-items-center">
        <div className="col-10 col-md-8 col-lg-6 loginContainer">
          <form
            className="form loginForm "
            id="login-form"
            onSubmit={(event) => {
              event.preventDefault();
              submitFunc()
            }}
            action=""
            method=""
          >
            <h1 className="text-center">Login</h1>

            <div className="form-group">
              <label for="phoneNo">Username:</label>
              <input
                type="text"
                className="form-control username"
                id="phoneNo"
                placeholder="Phone Number"
                name="phoneNo"
                onChange={(e)=>setPhoneNo(e.target.value)}
              />
              <div id="usernameError"></div>
            </div>

            <div className="form-group">
              <label for="password">Password:</label>
              <input
                type="password"
                className="form-control password"
                id="password"
                placeholder="Password"
                name="password"
                onChange={(e)=>setPassword(e.target.value)}
              />
              <div id="passwordError"></div>
            </div>
            <div id="errorDisplay">
            </div>
            <div
              id="forgotPassword"
              className="form-group d-flex justify-content-end mt-md-5"
            >
              <span className="">forgot password?</span>
            </div>
            <Button
              type="submit"
              color=""
              bgColor="var(--primary-color)"
              name="Login"
              id="loginBtn"
              className="btn btn-customized text-center px-5"
            />
          </form>
        </div>
      </div>
    </div>
  );
}
