import React from "react";

class ToggleButton extends React.Component{

    constructor(props){
        super(props)
        this.isClicked = false
        this.name = false
        // this.state ={isClicked: false, name : "ABEBE"}
    }


    clicked(){
        // alert("clicked")

            this.isClicked=!(this.isClicked) 
            this.name = !(this.name)


    }
    defaultPrev(event){
        event.preventDefault()
    }

    componentDidMount(){

    }

    render(){
        return (
            <div>
                <button onClick={()=>{this.clicked()}} id="reactBtn" >
                    {this.isClicked ? "Clicked" : "NOT CLICKED"}
                </button>
                <a href="#open" onClick={(event)=>{this.defaultPrev(event)}}>link</a>
                <h1>{(this.name) ? "ABEBE" : "KEBEDE" }</h1>
            </div>)
    }


}

export default ToggleButton;
