export function DeleteBranch(id) {
  let confirmation = window.confirm('you sure you want to delete the Branch');

  if (confirmation) {
    fetch(`${baseUrl}/${id}`, {
      method: 'DELETE',
    }).then((resp) => {
      resp.json();
    });

    window.location.reload();
  }
}
