import Button from '../Button';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import ErrorDisplay from '../ErrorDisplay';
import * as ReactDOM from 'react-dom/client';
import { CheckApiResponse } from '../functions/CheckApiResponse';
import PageHeading from '../PageHeading';


const baseUrl = 'http://127.0.0.1:5000/token:qwhu67fv56frt5drfx45e/staffs'

export function ShowStaffs({ staffList }) {
  return (
    <div className="">
    <PageHeading
    userType="Staff"
    // onclick={() => alert('yello')}
    fullData={staffList}
    data={selectedAdminList}
    setter={setSelectedAdminList}
  />      <table className="table table-striped">
        <tbody className="">
          <tr>
            <th>Id</th>
            <th>Full Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Branch</th>

            <th>Password</th>
            <th>
              <Link to="new">
                <Button
                  color=""
                  bgColor="var(--primary-color)"
                  name="Add Staff"
                  id="addAdminBtn"
                  className="btn px-4"
                />
              </Link>
            </th>
          </tr>
        </tbody>
        <tbody id="tableDataField">
          {staffList.map((item) => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.fullName}</td>
                <td>{item.phone}</td>
                <td>{item.email}</td>
                <td>{item.branch}</td>

                <td>{item.passwordHash}</td>
                <td>
                  <Link to="edit" state={{item:item}}>
                    <Button
                      color=""
                      bgColor=""
                      name="Edit"
                      id={item.id}
                      className="btn editBtn"
                    />
                  </Link>

                    <Button
                      color=""
                      bgColor=""
                      name="Delete"
                      id={item.id}
                      className="btn deleteButton ms-1"
                      onclick={()=>DeleteStaff(item.id)}
                    />
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export function NewStaff() {
  let navigate = useNavigate();

  const [id, setId] = useState();
  const [fullName, setFullName] = useState();
  const [phone, setPhone] = useState();
  const [email, setEmail] = useState();
  const [passwordHash, setPasswordHash] = useState();
  const [confirmpsdd, setconfirmpsdd] = useState();
  const [branch, setBranch] = useState();



  function newStaffAction() {
    if (confirmpsdd != passwordHash) {
      const root = ReactDOM.createRoot(document.getElementById('errorDisplay'));
      root.render(
        <ErrorDisplay
          design="text-danger"
          message="Your Password Doesn't Match!"
        />
      );
    }
    else{

    let headersList = {
      Accept: '/Application/json',
      'Content-Type': 'application/json',
      // "mode": 'no-cors',
    };

    let bodyContent = JSON.stringify({
      id,
      fullName,
      phone,
      email,
      passwordHash,
      branch,
    });

    fetch(`${baseUrl}`, {
      method: 'POST',
      body: bodyContent,
      headers: headersList,
    })
      .then(function (data) {

        CheckApiResponse(data,data.json())

      });
    }

  }

  return (
    <form
      className="form"
      id="add-admin-form"
      action=""
      method=""
      onSubmit={(e) => {
        newStaffAction();
        e.preventDefault();

      }}
    >
      <h1 id="formHeader">New Staff</h1>
      <div className="form-group">
        <label for="fullName">Full Name:</label>
        <input
          type="text"
          className="form-control "
          id="fullName"
          placeholder="Full Name"
          name="fullName"
          required
          oninvalid="input_error('fullName')"
          onChange={(e) => {
            setFullName(e.target.value);
          }}
        />
        <div id="fullNameError" className="errorOutput"></div>
      </div>
      <div className="form-group">
        <label for="phone">Phone:</label>
        <input
          type="tel"
          className="form-control tel"
          id="phone"
          placeholder="Phone"
          name="phone"
          required
          oninvalid="input_error('phone')"
          onChange={(e) => {
            setPhone(e.target.value);
          }}
        />
        <div id="phoneError" className="errorOutput"></div>
      </div>

      <div className="form-group">
        <label for="email">Email:</label>
        <input
          type="email"
          className="form-control email"
          id="email"
          placeholder="Email : example@email.com"
          name="email"
          required
          oninvalid="input_error('email')"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
        <div id="emailError" className="errorOutput"></div>
      </div>
      <div className="form-group">
      <label for="branch">Branch:</label>
      <input
        type="text"
        className="form-control password"
        id="branch"
        placeholder="Branch"
        name="branch"
        required
        oninvalid="input_error('branch')"
        onChange={(e) => {
          setBranch(e.target.value);
        }}
      />
      <div id="passwordError" className="errorOutput"></div>
    </div>
      <div className="form-group">
        <label for="password">Password:</label>
        <input
          type="password"
          className="form-control password"
          id="password"
          placeholder="Password"
          name="password"
          required
          oninvalid="input_error('password')"
          onChange={(e) => {
            setPasswordHash(e.target.value);
          }}
        />
        <div id="passwordError" className="errorOutput"></div>
      </div>
      <div className="form-group">
        <label for="confirmPassword" id="confirmPsdd">
          Confirm Password:
        </label>
        <input
          type="password"
          className="form-control password"
          id="confirmPassword"
          placeholder="Confirm Password"
          name="confirmPassword"
          required
          oninvalid="input_error('confirmPassword')"
          onChange={(e) => {
            setconfirmpsdd(e.target.value);
          }}
        />
        <div id="confirmPasswordError" className="errorOutput"></div>
      </div>
      <div id="errorDisplay"></div>
      <br />
      <button
        className="btn btn-customized buttonWider"
        id="addBtn"
        onSubmit="displayOverviewPage()"
      >
        Add
      </button>
    </form>
  );
}

export function DeleteStaff(id) {
  let confirmation = window.confirm('you sure you want to delete the staff');

  if (confirmation) {
    fetch(`${baseUrl}/${id}`, {
      method: 'DELETE',
    }).then((resp) => {
      resp.json();
    });

    window.location.reload();
  }
}

export function EditStaff() {

  const location = useLocation()
  const {item} = location.state

  let navigate = useNavigate();

  const [id, setId] = useState(item.id);
  const [fullName, setFullName] = useState(item.fullName);
  const [phone, setPhone] = useState(item.phone);
  const [email, setEmail] = useState(item.email);
  const [passwordHash, setPasswordHash] = useState(item.passwordHash);
  const [confirmPsdd, setconfirmPsdd] = useState(item.passwordHash);

  const [branch, setBranch] = useState(item.branch);

  function editStaffAction() {

    if (confirmPsdd != passwordHash) {
      const root = ReactDOM.createRoot(document.getElementById('errorDisplay'));


      root.render(
        <ErrorDisplay
          design="text-danger"
          message="Your Password Doesn't Match!"
        />
      );
    } else {
    let headersList = {
      "Accept": "/Application/json",
      "Content-Type": "application/json"
     }

     let bodyContent = JSON.stringify({
         id,
         fullName,
         phone,
         email,
         passwordHash,
         branch
     });

     fetch(`${baseUrl}/${item.id}`, { 
       method: "PATCH",
       body: bodyContent,
       headers: headersList
     }).then(function(data) {
       CheckApiResponse(data,data.json());
     })
    }

  }
  return (
    <form
      className="form"
      id="add-admin-form"
      action=""
      method=""
      onSubmit={(e) => {
        editStaffAction();
        e.preventDefault();
        // console.log(data)
      }}
    >
      <h1 id="formHeader">Edit Admin</h1>
      <div className="form-group">
        <label for="fullName">Full Name:</label>
        <input
          type="text"
          className="form-control "
          id="fullName"
          placeholder="Full Name"
          name="fullName"

          oninvalid="input_error('fullName')"
          onChange={(e) => {
            setFullName(e.target.value);
          }}
          value={fullName}

        />
        <div id="fullNameError" className="errorOutput"></div>
      </div>
      <div className="form-group">
        <label for="phone">Phone:</label>
        <input
          type="tel"
          className="form-control tel"
          id="phone"
          placeholder="Phone"
          name="phone"

          oninvalid="input_error('phone')"
          onChange={(e) => {
            setPhone(e.target.value);
          }}
          value={phone}


        />
        <div id="phoneError" className="errorOutput"></div>
      </div>

      <div className="form-group">
        <label for="email">Email:</label>
        <input
          type="email"
          className="form-control email"
          id="email"
          placeholder="Email : example@email.com"
          name="email"

          oninvalid="input_error('email')"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          value={email}


        />
        <div id="emailError" className="errorOutput"></div>
      </div>

      <div className="form-group">
        <label for="branch">Branch:</label>
        <input
          type="text"
          className="form-control tel"
          id="branch"
          placeholder="Branch"
          name="branch"

          oninvalid="input_error('branch')"
          onChange={(e) => {
            setBranch(e.target.value);
          }}
          value={branch}


        />
        <div id="phoneError" className="errorOutput"></div>
      </div>

      <div className="form-group">
        <label for="password">Password:</label>
        <input
          type="password"
          className="form-control password"
          id="password"
          placeholder="Password"
          name="password"

          oninvalid="input_error('password')"
          onChange={(e) => {
            setPasswordHash(e.target.value);
          }}
          value={passwordHash}


        />
        <div id="passwordError" className="errorOutput"></div>
      </div>
      <div className="form-group">
        <label for="confirmPassword" id="confirmPsdd">
          Confirm Password:
        </label>
        <input
          type="password"
          className="form-control password"
          id="confirmPassword"
          placeholder="Confirm Password"
          name="confirmPassword"

          oninvalid="input_error('confirmPassword')"
          onChange={(e) => {
            setconfirmPsdd(e.target.value);
          }}

          value={confirmPsdd}


        />
        <div id="confirmPasswordError" className="errorOutput"></div>
      </div>
      <div id="errorDisplay"></div>
      <br />
      <button
        className="btn btn-customized buttonWider"
        id="addBtn"
      >
        Save
      </button>
    </form>
  );
}
